class IndexController extends Controller
{
	public function index(Request $request, StandingsService $standingsService)
	{
		$country = $request->country;
		$league = $request->league;

		$id_league = League::select('id', 'type')->where(['name' => $league, 'country_name' => $country])->first();
		
		if($id_league !== null) {
			$current_league = LeagueSeason::select('season_year', 'standings')->where(['league_id' => $id_league->id])->first();

			$id = $id_league->id;
			$type = $id_league->type;
			$year = $current_league->season_year;
			$standings = $current_league->standings;
			
			if($standings !== 0) {
				
				return view('standings.table', compact('id', 'year', 'type', 'league', 'country'));
	
			} else {
				return redirect()->route('results', compact( 'league', 'country'));
			}
		} else {
			return abort(404);
		}
	}

