    public function index(Request $request)
    {
        $country = $request->country;
		$league = $request->league;

		$id_league = League::select('id')->where(['name' => $league, 'country_name' => $country])->first();
		
		if($id_league !== null) {
			$current_league = LeagueSeason::select('season_year', 'standings')->where(['league_id' => $id_league->id])->first();
			
			$id = $id_league->id;
			$type = $id_league->type;
			$year = $current_league->season_year;
			$standings = $current_league->standings;

			return view('standings.results', compact('id', 'year', 'standings', 'league', 'country'));
		} else {
			return abort(404);
		}
    }

