	private function shortFixturesByLeague($fixtures)
	{
		usort($fixtures, function($a, $b) {
			return strcmp($b['fixture']['date'], $a['fixture']['date']);
		});
	
		$groupedFixtures = [];
		$currentLeague = null;
		$currentRound = null;
	
		foreach ($fixtures as $fixture) {
			$league = 	[
							'id' => $fixture['league']['id'],
							'name' => $fixture['league']['name'],
							'country' => $fixture['league']['country'],
							'logo' => $fixture['league']['logo'],
							'flag' => $fixture['league']['flag'],
							'season' => $fixture['league']['season']
						];
			$round = $fixture['league']['round'];
			
			if ($league != $currentLeague) {
				$groupedFixtures[] = ['league' => $league, 'fixturesByRound' => []];
				$currentLeague = $league;
				$currentRound = null;
			}
			
			if ($round != $currentRound) {
				$groupedFixtures[count($groupedFixtures)-1]['fixturesByRound'][] = ['round' => $round, 'fixtures' => []];
				$currentRound = $round;
			}
			
			unset($fixture['league']);
			$groupedFixtures[count($groupedFixtures)-1]['fixturesByRound'][count($groupedFixtures[count($groupedFixtures)-1]['fixturesByRound'])-1]['fixtures'][] = $fixture;

			usort($groupedFixtures[count($groupedFixtures)-1]['fixturesByRound'][count($groupedFixtures[count($groupedFixtures)-1]['fixturesByRound'])-1]['fixtures'], function($a, $b) {
				return strcmp($b['fixture']['date'], $a['fixture']['date']);
			});
		}
	
		return $groupedFixtures;
	}
